import React from 'react'
import { connect } from 'react-redux'

import {getTotalBasketCount,getTotalBasketPrice} from 'reducers/selectors'
import { Link } from 'react-router-dom'
import s from './basketCart.module.css'


const BasketCart = ({totalBasketCount,totalPrice})=> {
    return (
        <div className={s.container}>
            <div className={s.basket}>
                <Link to='/basket' className={s.link} >
    <span>{totalBasketCount} Your item(s) - ${totalPrice}</span>
                </Link>
            </div>
        </div>
    )
}

const mapStateToProps = state => {
    return{
        totalBasketCount: getTotalBasketCount(state),
        totalPrice: getTotalBasketPrice(state)
    }
}

export default connect(mapStateToProps, null)(BasketCart)