import React, { Component } from 'react'
import { connect } from 'react-redux'
import s from './search.module.css'

import {searchPhone} from 'containers/actions/index'

class Search extends Component {
    constructor(props){
    super(props)
    this.state = {
        value: ''
    }
    this.handelSubmit=this.handelSubmit.bind(this)
    this.handelChange=this.handelChange.bind(this)
   
    }
    handelChange(event){
        this.setState ({
            value: event.target.value
        })
    }
   
    handelSubmit(event){
        
        event.preventDefault()
        this.props.searchPhone(this.state.value)
    }
 
    render(){
        return(
            <div className={s.container}>
               <form className={s.form} onSubmit={this.handelSubmit}
                    > 
                      <input className={s.inp} type='text' placeholder='Serach phone' onChange={this.handelChange}/>
                </form>
               
            </div>
        )
    }
}

 const mapDispatchToProps = {
    searchPhone
}

export default connect(null,mapDispatchToProps)(Search)